package com.superwatch.handlers;

import java.io.IOException;
import java.io.OutputStream;
import java.nio.charset.StandardCharsets;

import com.sun.net.httpserver.HttpExchange;
import com.sun.net.httpserver.HttpHandler;

public class FormHandler implements HttpHandler {

    @Override
    public void handle(HttpExchange exchange) throws IOException {
        String htmlForm = """
<!DOCTYPE html>
<html lang='fr'>
<head>
    <meta charset='UTF-8'>
    <meta name='viewport' content='width=device-width, initial-scale=1.0'>
    <title>SuperWatch</title>
    <style>
        * {
            padding: 0;
            margin: 0;
            box-sizing: content-box;
            font-size: 20px;
            color: white;

            -webkit-user-select: none;
            -webkit-touch-callout: none;
            -moz-user-select: none;
            -ms-user-select: none;
            user-select: none;
        }

        html {
            background: rgb(2, 0, 36);
            background: linear-gradient(180deg,
                rgba(2, 0, 36, 1) 0%,
                rgba(7, 7, 120, 1) 35%,
                rgba(2, 0, 36, 1) 100%);
            min-height: 100%;
            font-family: Arial, Helvetica, sans-serif;
        }

        body {
            font-family: Arial, sans-serif;
            display: flex;
            justify-content: center;
            align-items: center;
            height: 100vh;
            margin: 0;
        }

        .container {
            text-align: center;
            padding: 40px;
            border-radius: 16px;
        }

        h1 {
            color: white;
            font-size: xx-large;
            margin-bottom: 50px;
        }

        .buttons {
            display: flex;
            justify-content: center;
        }

        .buttons form {
            display: flex;
            flex-direction: row;
            gap: 40px;
            padding: 20px;
        }

        .button {
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            width: 180px;
            height: 180px;
            font-size: 18px;
            font-weight: bold;
            cursor: pointer;
            border-radius: 12px;
            transition: transform 0.3s ease, box-shadow 0.3s ease;
            border: 2px solid;
            padding: 20px;
            background-color: rgba(0, 120, 215, 0.7);
            color: white;
            border-color: #0078d7;
            margin: 0;
        }

        .button:hover {
            transform: scale(1.1);
            box-shadow: 0 10px 20px rgba(0, 0, 0, 0.3);
        }

        .logo {
            font-size: 64px;
            margin-bottom: 15px;
        }

        .button-text {
            margin-top: 10px;
        }

        .logo {
            width: 80px;
            height: 80px;
            margin-bottom: 10px;
            background-repeat: no-repeat;
            background-position: center;
            display: flex;
            align-items: center;
            justify-content: center;
        }

        .image-windows-logo, .image-linux-logo {
            height: 100%;
        }
    </style>
    <style>
            .hidden {
            opacity: 0;
            visibility: hidden;
            transition: all 0.5s ease;
        }

        .loading-screen {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            display: flex;
            flex-direction: column;
            align-items: center;
            justify-content: center;
            background: rgba(0, 0, 0, 0.9);
            border-radius: 16px;
            opacity: 0;
            visibility: hidden;
            transition: all 0.5s ease 0.3s;
        }

        .loading-screen.active {
            opacity: 1;
            visibility: visible;
        }

        .spinner {
            width: 50px;
            height: 50px;
            border: 4px solid #0078d7;
            border-top: 4px solid transparent;
            border-radius: 50%;
            animation: spin 1s linear infinite;
            margin-bottom: 20px;
        }

        @keyframes spin {
            0% { transform: rotate(0deg); }
            100% { transform: rotate(360deg); }
        }

        .loading-text {
            font-size: 24px;
            color: #0078d7;
            text-transform: uppercase;
            letter-spacing: 2px;
        }
    </style>
</head>
<body>
    <div class='container'>
        <h1>Choisissez votre syst√®me d'exploitation</h1>
        <div class='buttons'>
            <form action='/install' method='POST' id="osForm">
                <button type='submit' name='os' value='windows' class='button'>
                    <div class='logo'><img class='image-windows-logo' src='data:image/png;base64,'></div>
                    <div class='button-text'>Windows</div>
                </button>
                <button type='submit' name='os' value='linux' class='button'>
                    <div class='logo'><img class='image-linux-logo' src='data:image/png;base64,'></div>
                    <div class='button-text'>Linux</div>
                </button>
            </form>
        </div>
        <div class="loading-screen">
            <div class="spinner"></div>
            <div class="loading-text">En cours d'installation</div>
        </div>
    </div>
    <script>
        document.querySelectorAll('.button').forEach(button => {
            button.addEventListener('click', async function(e) {
                const loadingScreen = document.querySelector('.loading-screen');
                const mainContent = document.querySelector('.container');
                
                // Activer l'animation imm√©diatement
                mainContent.style.opacity = '0';
                loadingScreen.classList.add('active');

                try {
                    const formData = new URLSearchParams();
                    formData.append('os', this.value);

                    const response = await fetch('/install', {
                        method: 'POST',
                        body: formData,
                        headers: {
                            'Content-Type': 'application/x-www-form-urlencoded'
                        }
                    });

                    const result = await response.text();
                    
                    if (!response.ok) throw new Error(result);

                    // Mise √† jour du texte apr√®s succ√®s
                    document.querySelector('.loading-text').textContent = "Installation r√©ussie !";
                    await new Promise(resolve => setTimeout(resolve, 2000));
                    
                    // Redirection ou autre action
                    window.location.reload();

                } catch (error) {
                    loadingScreen.style.backgroundColor = 'rgba(80, 0, 0, 0.95)';
                    document.querySelector('.spinner').style.display = 'none';
                    document.querySelector('.loading-text').innerHTML = 
                        `Erreur : ${error.message}<br><small>R√©essayez ou contactez le support</small>`;
                }
            });
        });
    </script>

    <script>
        function selectWindows(os) {
            alert('Vous avez choisi : Windows');
        }
        function selectLinux(os) {
            alert('Vous avez choisi : Linux');
        }
    </script>
    <script type='text/javascript' src='https://code.jquery.com/jquery-1.7.1.min.js'></script>
    <script src='assets/js/navbar.js'></script>
</body>
</html>
            """;
            
            

        byte[] responseBytes = htmlForm.getBytes(StandardCharsets.UTF_8);
        exchange.sendResponseHeaders(200, responseBytes.length);
        OutputStream os = exchange.getResponseBody();
        os.write(responseBytes);
        os.close();
    }
}
